<?php	//Database connection	require 'db_connect.php';	try {		// prepare query		$query = "SELECT ngo_name, district, address ,contact_person, phone_num FROM tbl_name WHERE district = ?";						$stmt = $con->prepare($query);		// this is the first question mark above		$stmt->bindParam(1, $_REQUEST['district']);		// execute our query		$stmt->execute();		// store retrieved row to a variable		// $row = $stmt->fetch(PDO::FETCH_ASSOC);		echo "<table>";			echo "<thead>";				echo "<td>NGO Name: </td>";				echo "<td>Address: </td>";				echo "<td>Contact Person</td>";				echo "<td>Phone No.</td>";			echo "</thead>";			while ($row = $stmt->fetch(PDO::FETCH_ASSOC)){				// values to fill up our table				$ngos = $row['ngo_name'];				$address = $row['address'];				$contact_person= $row['contact_person'];				$phone = $row['phone_num'];				//$username = $row[ausername'];				// our table				echo "<tr>";					echo "<td><b><i>{$ngos}</i></b></td>";					echo "<td>{$address}</td>";					echo "<td>{$contact_person}</td>";					echo "<td>{$phone}</td>";				echo "</tr>";			// echo "<tr>";			// echo "<td>Username: </td>";			// echo "<td>{$username}</td>";			// echo "</tr>";			}				//End of table			echo "</table>";	}	catch(PDOException $exception){		// to handle error		echo "Error: " . $exception->getMessage();	}?>